import React, { Component } from "react";
import "./ProductContainer.scss";

class ProductContainer extends Component {
  constructor() {
    super();
    this.state = {
      heartImg: [
        "https://s3.ap-northeast-2.amazonaws.com/cdn.wecode.co.kr/bearu/heart.png",
        "/images/productList/heart_fill.png",
      ],
      heartBoolean: false,
      current_idx: null,
    };
  }

  handleHeart = (e) => {
    this.setState({
      heartBoolean: !this.state.heartBoolean,
    });
  };

  changeImage = (idx) => {
    this.setState({
      current_idx: idx,
    });
  };

  render() {
    const { heartImg, heartBoolean } = this.state;
    const { id, imgUrl, name, price, colorList } = this.props;
    const [frontImg, backImg] = imgUrl;

    return (
      <div className="ProductContainer">
        <div className="productImage" key={this.props.id}>
          <img
            alt="heartImg"
            onClick={this.handleHeart}
            className="heartImgEmpty"
            src={heartImg[0]}
          />
          <img
            alt="heartImg"
            onClick={this.handleHeart}
            className={
              heartBoolean ? "heartImgFillValid" : "heartImgFillUnvalid"
            }
            src={heartImg[1]}
          />

          <div className="mainShoesImage">
            <img alt="frontShoesImg" className="frontShoesImg" src={frontImg} />
            <img alt="backShoesImg" className="backShoesImg" src={backImg} />
          </div>

          {colorList.map((colorEl, idx) => {
            const { id, imgUrl } = colorEl;
            return (
              <div
                key={id}
                className={
                  this.state.current_idx === idx
                    ? "showcolorShoesImage"
                    : "colorShoesImage"
                }
              >
                <img src={imgUrl[0]} className="colorShoesImageFront" />
                <img src={imgUrl[1]} className="colorShoesImageBack" />
              </div>
            );
          })}
        </div>
        <p>{name}</p>
        <p>{Number(price).toLocaleString("en")}원</p>
        <div className="colorList">
          <div className="colorString">
            <span>{colorList.length}컬러</span>
          </div>
          {colorList.map((colorEl, idx) => {
            const { color } = colorEl;
            return (
              <div
                key={id}
                style={{ backgroundColor: color }}
                className="colorCircle"
                onMouseOver={() => this.changeImage(idx)}
                onMouseOut={() => this.changeImage(null)}
              >
                <div className="selectColor"></div>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default ProductContainer;
