import React, { Component } from "react";
import "./ProductColors.scss";

class ProductColors extends Component {
  render() {
    const { colorList } = this.props;
    return (
      <div className="ProductColors">
        <div className="colorString">
          <span>{colorList.length}컬러</span>
        </div>
        {colorList.map((colorEl, idx) => {
          const { color } = colorEl;
          return (
            <div
              key={id}
              style={{ backgroundColor: color }}
              className="colorCircle"
              onMouseOver={() => this.changeImage(idx)}
              onMouseOut={() => this.changeImage(null)}
            >
              <div className="selectColor"></div>
            </div>
          );
        })}
      </div>
    );
  }
}

export default ProductColors;
