import React, { Component } from "react";
import "./FilterColor.scss";

class FilterColor extends Component {
  constructor() {
    super();
    this.state = {
      selectColor: false,
    };
  }

  render() {
    const {
      hideFilter,
      hideFilterImage,
      filterImage,
      productColor,
    } = this.props;

    return (
      <div className="FilterColor">
        <div onClick={() => hideFilter(2)} className="hideBar">
          <span>색상</span>
          <div
            className={hideFilterImage[2] ? "hideFilterImage" : "filterImage"}
          >
            <img className="minusImage" src={filterImage[0]} />
            <img className="plusImage" src={filterImage[1]} />
          </div>
        </div>
        <div className={hideFilterImage[2] ? "hideTypeOfColor" : "typeOfColor"}>
          {productColor.map((Color) => (
            <div
              onClick={this.handleColor}
              style={{ backgroundColor: Color }}
              className="colorCircle"
            ></div>
          ))}
        </div>
      </div>
    );
  }
}

export default FilterColor;
