import React, { Component } from "react";
import { productColor, productSize, productSilluet } from "./filterContents";
import "./FilterVerticalBar.scss";

export class FilterVerticalBar extends Component {
  constructor() {
    super();
    this.state = {
      productColor: [],
      productSize: [],
      productSilluet: [],
      filterImgae: [
        "data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg'  viewBox='0 0 40 5'%3E%3Cpath fill='%23000' d='M0 0h40v4.5H0z' /%3E%3C/svg%3E",
        "data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg'  viewBox='0 0 40 40'%3E%3Cpath fill='%23000' d='M40 17.857H22.143V0h-4.286v17.857H0v4.286h17.857V40h4.286V22.143H40z' /%3E%3C/svg%3E",
      ],
      hideFilterImage: [false, false, false, false, false],
    };
  }

  componentDidMount() {
    this.setState({
      productColor: productColor,
      productSize: productSize,
      productSilluet: productSilluet,
    });
  }

  hideFilter = (e) => {
    // this.state.hideFilterImage[e] = !this.state.hideFilterImage[e];

    this.setState({
      hideFilterImage: [
        ...this.state.hideFilterImage.splice(0, e - 1),
        !this.state.hideFilterImage[e],
        ...this.state.hideFilterImage.splice(e + 1),
      ],
    });
    // () => {
    //   console.log(this.state.hideFilterImage[0]);
    // };
  };

  render() {
    const {
      productColor,
      productSize,
      productSilluet,
      filterImgae,
      hideFilterImage,
    } = this.state;
    return (
      <div className="FilterVerticalBar">
        <div className="filterGender">
          <div onClick={() => this.hideFilter(0)} className="hideBar">
            <span>구분</span>
            <div
              className={hideFilterImage[0] ? "hideFilterImage" : "filterImage"}
            >
              <img className="minusImage" src={filterImgae[0]} />
              <img className="plusImage" src={filterImgae[1]} />
            </div>
          </div>
          <div className="kindsOfGender">
            <a>남성</a>
            <a>여성</a>
          </div>
        </div>
        <div className="filterType">
          <div onClick={() => this.hideFilter(1)} className="hideBar">
            <span>제품 타입</span>
            <div
              // className="filterImage"
              className={hideFilterImage[1] ? "hideFilterImage" : "filterImage"}
            >
              <img className="minusImage" src={filterImgae[0]} />
              <img className="plusImage" src={filterImgae[1]} />
            </div>
          </div>
          <div className="kindsOfType">
            <a>뮬</a>
            <a>샌들&뮬</a>
            <a>스니커즈</a>
          </div>
        </div>
        <div className="filterColor">
          <div onClick={() => this.hideFilter(2)} className="hideBar">
            <span>색상</span>
            <div
              // className="filterImage"
              className={hideFilterImage[2] ? "hideFilterImage" : "filterImage"}
            >
              <img className="minusImage" src={filterImgae[0]} />
              <img className="plusImage" src={filterImgae[1]} />
            </div>
          </div>
          <div className="typeOfColor">
            {productColor.map((el) => (
              <div
                style={{ backgroundColor: el }}
                className="colorCircle"
              ></div>
            ))}
          </div>
        </div>
        <div className="filterSize">
          <div onClick={() => this.hideFilter(3)} className="hideBar">
            <span>사이즈</span>
            <div
              // className="filterImage"
              className={hideFilterImage[3] ? "hideFilterImage" : "filterImage"}
            >
              <img className="minusImage" src={filterImgae[0]} />
              <img className="plusImage" src={filterImgae[1]} />
            </div>
          </div>
          <div className="typeOfSize">
            {productSize.map((el) => (
              <div className="sizeSquare">{el}</div>
            ))}
          </div>
        </div>
        <div className="filterSilluet">
          <div onClick={() => this.hideFilter(4)} className="hideBar">
            <span>실루엣</span>
            <div
              // className="filterImage"
              className={hideFilterImage[4] ? "hideFilterImage" : "filterImage"}
            >
              <img className="minusImage" src={filterImgae[0]} />
              <img className="plusImage" src={filterImgae[1]} />
            </div>
          </div>
          <div className="typeOfSilluet">
            {productSilluet.map((el) => (
              <div className="silluetSquare">{el.name}</div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default FilterVerticalBar;
