import React from "react";
import FilterVerticalBar from "./Components/FilterVerticalBar/FilterVerticalBar";
import ProductListMain from "./Components/ProductListMain/ProductListMain";
import "./ProductList.scss";

export class ProductList extends React.Component {
  constructor() {
    super();
    this.state = {
      productInfo: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:3000/data/ProductList/ProductList.json", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((res) => {
        this.setState({
          productInfo: res.productInfo,
        });
      });
  }

  render() {
    return (
      <div className="ProductList">
        <nav>nav 컴포넌트 예정</nav>
        <header>
          {/* <title>SHOES</title> */}
          {/* <div className="title">SHOES</div> */}
          <span className="shoesTitle">SHOES</span>
          <img
            alt="headerImges"
            src="https://image.converse.co.kr/cmsstatic/structured-content/15400/D-Converse-SP20-PWH-Best-Sellers-.jpg"
          />
        </header>
        <div className="FilterHorizontalBar">
          <div>
            <span>필터 숨기기</span>
          </div>
          <div>
            <span>총 0개의 상품</span>
          </div>
          <div>
            <span>신상품순sss</span>
          </div>
        </div>
        <div className="bottomOfPage">
          <FilterVerticalBar />
          <div className="ProductListContainer">
            {this.state.productInfo.map((el) => (
              <ProductListMain id={el.id} src={el.src} />
            ))}
            {/* <ProductListMain /> */}
          </div>
        </div>
      </div>
    );
  }
}

export default ProductList;
