import React, { Component } from "react";
import "./ProductColors.scss";

class ProductColors extends Component {
  constructor() {
    super();
    this.state = {
      // hideColorString: false,
      colorListValid: false,
    };
  }

  colorListValid = () => {
    this.setState({
      // hideColorString: !this.state.hideColorString,
      colorListValid: !this.state.colorListValid,
    });
  };

  render() {
    const { id, colorList, changeImage } = this.props;
    const { colorListValid } = this.state;
    return (
      <div className="ProductColors">
        <div
          className="colorString"
          // className={hideColorString ? "hideColorString" : "colorString"}
        >
          <span>{colorList.length}컬러</span>
        </div>
        <div
          // className="colorList"
          onMouseOver={this.colorListValid}
          onMouseOut={this.colorListValid}
          className={colorListValid ? "colorListValid" : "colorListUnvalid"}
        >
          {colorList.map((colorEl, idx) => {
            const { color } = colorEl;
            return (
              <div
                key={id}
                style={{ backgroundColor: color }}
                className="colorCircle"
                onMouseOver={() => changeImage(idx)}
                onMouseOut={() => changeImage(null)}
              >
                <div className="selectColorUnvalid"></div>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default ProductColors;
