import React, { Component } from "react";
import ProductMainImage from "../../../ProductMainImage";
import "./ProductColors.scss";

class ProductColors extends Component {
  fixedImage = (colorEl, section) => {
    ProductMainImage.MainImg[section].id = colorEl.id;
    ProductMainImage.MainImg[section].ImgUrl = colorEl.image_url;
    console.log(colorEl);
    console.log(section);
  };

  render() {
    const {
      id,
      colorList,
      changeImage,
      // fixedImage,
      colorListValid,
      selectColorValid,
      section,
    } = this.props;
    return (
      <div className="ProductColors">
        <div className="colorString">
          <span>{colorList.length}컬러</span>
        </div>
        <div className={colorListValid ? "colorListValid" : "colorListUnvalid"}>
          {colorList.map((colorEl, idx) => {
            const { color } = colorEl;
            return (
              <div
                key={id}
                style={{ backgroundColor: color }}
                className="colorCircle"
                onClick={() => this.fixedImage(colorEl, section)}
                onMouseOver={() => changeImage(idx)}
                onMouseOut={() => changeImage(null)}
              >
                <div
                  // className="selectColorUnvalid"
                  className={
                    selectColorValid === idx
                      ? "selectColorValid"
                      : "selectColorUnvalid"
                  }
                ></div>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default ProductColors;
